import React, { useState } from "react";
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

import "./Map.css";

export default function Map(props) {
    const mapStyles = {        
        height: "50vh",
        width: "50%"
    };

    // Initial center position, doesn't change on marker reposition
    const defaultCenter = {
        lat: 41.3851, 
        lng: 2.1734
    };

    // State for the marker position
    const [markerPosition, setMarkerPosition] = useState(defaultCenter);

    const onMapClick = (event) => {
        // Update marker position without changing the map's center
        setMarkerPosition({
            lat: event.latLng.lat(),
            lng: event.latLng.lng()
        });
    };

    return (
        <LoadScript googleMapsApiKey="AIzaSyCnBKASLNJoWMGVatxoo456e7WO9ELADAw">
            <GoogleMap
                mapContainerStyle={mapStyles}
                zoom={13}
                center={defaultCenter} // Keep the center constant
                onClick={onMapClick}
            >
                <Marker position={markerPosition}/>
            </GoogleMap>
        </LoadScript>
    );
}
