import React, { useState } from "react";
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

export default function Map(props) {
    const mapStyles = {        
        height: "50vh",
        width: "50%"
    };

    const defaultCenter = {
        lat: 41.3851, 
        lng: 2.1734
    };

    // State for the marker position
    const [markerPosition, setMarkerPosition] = useState(null);

    // Use this state to explicitly change the map's center when needed
    const mapCenter = defaultCenter;

    const onMapClick = (event) => {
        setMarkerPosition({
            lat: event.latLng.lat(),
            lng: event.latLng.lng()
        });
        // Consider updating mapCenter here if you want to re-center on marker placement
    };

    const mapOptions = {
        streetViewControl: false, // Disable Street View
        mapTypeControl: false, // Disable Map/Satellite toggle
        fullscreenControl: false, // Disable Full Screen option
        clickableIcons: false, // Disable POI Clicks
        gestureHandling: 'auto'
    };

    return (
        <LoadScript googleMapsApiKey="AIzaSyCnBKASLNJoWMGVatxoo456e7WO9ELADAw">
            <GoogleMap
                mapContainerStyle={mapStyles}
                zoom={13}
                center={mapCenter} // Use dynamic center state
                onClick={onMapClick}
                options={{ gestureHandling: 'auto' }}
            >
                <Marker position={markerPosition}/>
            </GoogleMap>
        </LoadScript>
    );
}
