import React from "react";
import "./StreetView.css";
import { GoogleMap, LoadScript, StreetViewPanorama } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

const panoramaOptions = {
  position: { lat: 42.345573, lng: -71.098326 }, // Example coordinates
  pov: { heading: 34, pitch: 10 }, // Point of view (direction and angle)
  zoom: 1,
};

export default function StreetView(props) {
    const { googleMapsApiKey } = props; // Assume API key is passed via props

    return (
        <div className="streetview__wrapper">
            <LoadScript googleMapsApiKey={googleMapsApiKey}>
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    zoom={10}
                    center={panoramaOptions.position}
                >
                    <StreetViewPanorama options={panoramaOptions} />
                </GoogleMap>
            </LoadScript>
        </div>
    );
}
