import React, { useState } from 'react';
import GoogleStreetview from 'react-google-streetview';

import "./StreetView.css";

export default function StreetView(props) {
    const googleMapsApiKey = props.googleMapsApiKey;

    // Initialize state for position and pov
    const [position, setPosition] = useState({lat: 38.90768333333333, lng: -77.07220555555556});
    const [pov, setPov] = useState({heading: 230, pitch: 14});

    // Handler for position change
    const handlePositionChanged = (data) => {
        setPosition(data.position); // Update position state
    };

    // Handler for POV change
    const handlePovChanged = (data) => {
        setPov(data.pov); // Update pov state
    };

    const streetViewPanoramaOptions = {
        position: position,
        pov: pov,
        zoom: 1,
        disableDefaultUI: true,
        addressControl: false,
        clickToGo: false,
        showRoadLabels: false,
        enableCloseButton: false,
        motionTracking: false,
        motionTrackingControl: false,
        linksControl: false
    };

    return (
        <div className="streetview__wrapper">
            <GoogleStreetview
                apiKey={googleMapsApiKey}
                streetViewPanoramaOptions={streetViewPanoramaOptions}
                onPositionChanged={handlePositionChanged}
                onPovChanged={handlePovChanged}
                style={{
                    width: '100%',
                    height: '100%'
                }}
            />
        </div>
    );
}
