import React from "react";
import "./StreetView.css";
import { StreetViewPanorama } from '@react-google-maps/api';

const containerStyle = {
  width: '100vw', // Full viewport width
  height: '100vh', // Full viewport height
};

const panoramaOptions = {
  position: { lat: 42.345573, lng: -71.098326 }, // Example coordinates
  pov: { heading: 34, pitch: 10 }, // Point of view (direction and angle)
  zoom: 1,
};

export default function StreetView() {
    return (
        <div className="streetview__wrapper">
            <StreetViewPanorama options={panoramaOptions} containerStyle={containerStyle} />
        </div>
    );
}
