import React, { useState, useRef, useEffect } from "react";
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

export default function Map(props) {
    const mapStyles = {        
        height: "50vh",
        width: "50%"
    };

    const defaultCenter = {
        lat: 38.90899666691537, 
        lng: -77.067332462228,
    };

    const [markerPosition, setMarkerPosition] = useState(null);
    const mapRef = useRef(null);
    const maxZoomLevel = 17;

    const onMapLoad = (map) => {
        mapRef.current = map;
    };

    const enforceMaxZoom = () => {
        if (mapRef.current.getZoom() > maxZoomLevel) {
            mapRef.current.setZoom(maxZoomLevel);
        }
    };

    useEffect(() => {
        if (mapRef.current) {
            const zoomListener = mapRef.current.addListener('zoom_changed', enforceMaxZoom);
            return () => google.maps.event.removeListener(zoomListener);
        }
    }, []);

    const onMapClick = (event) => {
        setMarkerPosition({
            lat: event.latLng.lat(),
            lng: event.latLng.lng()
        });
    };

    const mapOptions = {
        mapTypeControl: false, 
        fullscreenControl: false, 
        clickableIcons: false
    };

    return (
        <LoadScript googleMapsApiKey="AIzaSyBfr_271qEHFo2CUz8YAwlSmfAYIuocGdU">
            <GoogleMap
                mapContainerStyle={mapStyles}
                zoom={13}
                center={defaultCenter}
                onClick={onMapClick}
                onLoad={onMapLoad}
                options={mapOptions}
            >
                {markerPosition && <Marker position={markerPosition}/>}
            </GoogleMap>
        </LoadScript>
    );
}
