import React, { useState } from "react";
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

export default function Map(props) {
    const mapStyles = {        
        height: "50vh",
        width: "50%"
    };

    const defaultCenter = {
        lat: 38.90899666691537, 
        lng: -77.067332462228,
    };

    // State for the marker position
    const [markerPosition, setMarkerPosition] = useState(null);
    const [mapCenter, setMapCenter] = useState(defaultCenter);
    const mapRef = React.useRef(null);

    const onMapLoad = (map) => {
        mapRef.current = map;
    };

    const onMapClick = (event) => {
        console.log(event.latLng.lat())
        console.log(event.latLng.lng())
        setMarkerPosition({
            lat: event.latLng.lat(),
            lng: event.latLng.lng()
        });
    };

    const onBoundsChanged = () => {
        const map = mapRef.current;

        if (!map) return; // Check if the map is loaded

        // Define the boundaries of your area here
        const allowedBounds = new window.google.maps.LatLngBounds(
            new window.google.maps.LatLng(38.92297059229842, -77.0888081871093), // Southwest coordinates
            new window.google.maps.LatLng(38.89503812156255, -77.04347055671803)  // Northeast coordinates
        );

        if (!allowedBounds.contains(map.getCenter())) {
            // If out of bounds, reset the center
            let newCenter = map.getCenter();

            if (map.getCenter().lat() > allowedBounds.getNorthEast().lat()) {
                newCenter = new window.google.maps.LatLng(allowedBounds.getNorthEast().lat(), newCenter.lng());
            }
            if (map.getCenter().lat() < allowedBounds.getSouthWest().lat()) {
                newCenter = new window.google.maps.LatLng(allowedBounds.getSouthWest().lat(), newCenter.lng());
            }
            if (map.getCenter().lng() > allowedBounds.getNorthEast().lng()) {
                newCenter = new window.google.maps.LatLng(newCenter.lat(), allowedBounds.getNorthEast().lng());
            }
            if (map.getCenter().lng() < allowedBounds.getSouthWest().lng()) {
                newCenter = new window.google.maps.LatLng(newCenter.lat(), allowedBounds.getSouthWest().lng());
            }
            map.panTo(newCenter);
        }
    };


    // Map options
    const mapOptions = {
        mapTypeControl: false, // Disable Map/Satellite toggle
        fullscreenControl: false, // Disable Full Screen option
        clickableIcons: false,
    };



    return (
        <LoadScript googleMapsApiKey="AIzaSyBfr_271qEHFo2CUz8YAwlSmfAYIuocGdU">
            <GoogleMap
                mapContainerStyle={mapStyles}
                zoom={13}
                center={mapCenter}
                onClick={onMapClick}
                options={mapOptions}
                onBoundsChanged={onBoundsChanged}
            >
                {markerPosition && <Marker position={markerPosition}/>}
            </GoogleMap>
        </LoadScript>
    );
}
