import React from "react";
import "./StreetView.css";
import { StreetViewPanorama } from '@react-google-maps/api';

export default function StreetView() {
    // Directly use the coordinates and POV as props
    const position = { lat: 42.345573, lng: -71.098326 }; // Example coordinates
    const pov = { heading: 34, pitch: 10 }; // Point of view (direction and angle)

    console.log(position);

    return (
        <div className="streetview__wrapper">
            <StreetViewPanorama
            key={`${position.lat}-${position.lng}`} // Unique key based on position
            position={position}
            pov={pov}
            zoom={1}
            />

        </div>
    );
}
