import React, { useState, useRef } from "react";
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

export default function Map(props) {
    const mapStyles = {        
        height: "50vh",
        width: "50%"
    };

    const defaultCenter = {
        lat: 38.90899666691537, 
        lng: -77.067332462228,
    };

    const [markerPosition, setMarkerPosition] = useState(null);
    const mapRef = useRef(null);
    const minZoomLevel = 14;

    const onMapLoad = (map) => {
        mapRef.current = map;
    };

    const onMapClick = (event) => {
        if (mapRef.current) {
            // Temporarily disable map interactions
            mapRef.current.setOptions({ gestureHandling: "none" });

            // Set marker position
            setMarkerPosition({
                lat: event.latLng.lat(),
                lng: event.latLng.lng()
            });

            // Re-enable interactions after a short delay
            setTimeout(() => {
                mapRef.current.setOptions({ gestureHandling: "auto" });
            }, 10); // Delay can be adjusted as needed
        }
    };

    const mapOptions = {
        mapTypeControl: false, 
        fullscreenControl: false, 
        clickableIcons: false,
        minZoom: minZoomLevel // Set the minimum zoom level
    };

    return (
        <LoadScript googleMapsApiKey="AIzaSyBfr_271qEHFo2CUz8YAwlSmfAYIuocGdU">
            <GoogleMap
                mapContainerStyle={mapStyles}
                zoom={14}
                center={defaultCenter}
                onClick={onMapClick}
                onLoad={onMapLoad}
                options={mapOptions}
            >
                {markerPosition && <Marker position={markerPosition}/>}
            </GoogleMap>
        </LoadScript>
    );
}
