import React, { useState } from "react";
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

export default function Map(props) {
    const [mapCenter, setMapCenter] = useState({ lat: 38.90899666691537, lng: -77.067332462228 });
    const [markerPosition, setMarkerPosition] = useState(null);
    const minZoomLevel = 14;

    const onMapClick = (event) => {
        let latitudeDist = event.latLng.lat() - props.imgCoords.latitude;
        let longitudeDist = event.latLng.lng() + props.imgCoords.longitude;

        const dist = calculateDistance(props.imgCoords.latitde, props.imgCoords.longitude, event.latLng.lat(), event.latLng.lng());
        console.log(`distance: ${dist} meters`);

        setMarkerPosition({
            lat: event.latLng.lat(),
            lng: event.latLng.lng()
        });
    };

    const mapOptions = {
        mapTypeControl: false,
        fullscreenControl: false,
        clickableIcons: false,
        disableDoubleClickZoom: true,
        minZoom: minZoomLevel
    };

    return (
        <LoadScript googleMapsApiKey="AIzaSyBfr_271qEHFo2CUz8YAwlSmfAYIuocGdU">
            <GoogleMap
                mapContainerStyle={{ height: "50vh", width: "50%", outline: "none",}}
                zoom={14}
                center={mapCenter}
                onClick={onMapClick}
                options={mapOptions}
            >
                {markerPosition && <Marker position={markerPosition} />}
            </GoogleMap>
        </LoadScript>
    );
}

function calculateDistance(lat1, lon1, lat2, lon2) {
    const R = 6371e3; // Radius of the Earth in meters
    const φ1 = lat1 * Math.PI / 180; // φ, λ in radians
    const φ2 = lat2 * Math.PI / 180;
    const Δφ = (lat2 - lat1) * Math.PI / 180;
    const Δλ = (lon2 - lon1) * Math.PI / 180;

    // Haversine formula
    const a = Math.sin(Δφ / 2) * Math.sin(Δφ / 2) +
              Math.cos(φ1) * Math.cos(φ2) *
              Math.sin(Δλ / 2) * Math.sin(Δλ / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c; // Distance in meters

    return distance;
}
